<script type="text/javascript">
var penulis="<?php echo $this->session->userdata('level');?>";
var url='<?php echo site_url(); ?>';

$(window).load(function(){
	$.ajax({
        'url': '<?php echo site_url("kategori/getkategori");?>',
        'dataType': 'json',
        'success': function(data){
        	//console.log(data);
        	if(data.data!=null){
        		isi(data.data);
        	}
        }
    });

    form_tambah_kategori_reset();
});

$(document).ready(function(){
	$('#form-tambah-kategori').submit(function(e){
		e.preventDefault();
		$.ajax({
	        'type': 'POST',
	        'url': '<?php echo site_url("kategori/addkategori");?>',
	        'dataType': 'json',
	        'data': $(this).serialize(),
	        success: function(data){
	        	//console.log(data);
	        	if(data.data!=null){
					isi(data.data);
					var notification = alertify.notify(data.psn, data.wrng, 5, function(){});
                }else{
                    alertify.error('ERROR!');
                }
	        }
	    });
	    form_tambah_kategori_reset();
	});

	$('#form-hapus-kategori-massal').submit(function(e){
		e.preventDefault();
		$.ajax({
	        'type': 'POST',
	        'url': '<?php echo site_url("kategori/delkategorimassal");?>',
	        'dataType': 'json',
	        'data': $(this).serialize(),
	        success: function(data){
	        	//console.log(data);
	        	if(data.data!=null){
					isi(data.data);
					var notification = alertify.notify(data.psn, data.wrng, 5, function(){});
                }else{
                    alertify.error('ERROR!');
                }
	        }
	    });
	    form_tambah_kategori_reset();
	});
});

function cari_kategori_(){
	console.log($('#cari-kategori').val());
	$.ajax({
        'type': 'POST',
        'url': '<?php echo site_url("kategori/searchkategori");?>',
        'dataType': 'json',
        'data': 'cari_kategori='+$('#cari-kategori').val(),
        success: function(data){
        	//console.log(data);
        	if(data.data!=null){
				isi(data.data);
			}
        }
    });
    $('#check-kategori-all-bawah').prop('checked', false);
    $('#check-kategori-all-atas').prop('checked', false);
}

function form_tambah_kategori_reset(){
	$('#id-kategori').val('');
	$('#nama-kategori').val('');
	$('#slug-kategori').val('');
	$('#induk-kategori').val('0');
	$('#deskripsi-kategori').val('');
	$('#icon-menu').val('');
}

function row_actions_over(id){
	$('#row-actions-'+id).show();
	$('#br-'+id).hide();
}

function row_actions_blur(id){
	$('#row-actions-'+id).hide();
	$('#br-'+id).show();
}

function check_kategori_all_atas(){
	if(document.getElementById('check-kategori-all-atas').checked==true){
		$('.check-kategori').prop('checked', true);
		$('#check-kategori-all-bawah').prop('checked', true);
	}else{
		$('.check-kategori').prop('checked', false);
		$('#check-kategori-all-bawah').prop('checked', false);
	}
}

function check_kategori_all_bawah(){
	if(document.getElementById('check-kategori-all-bawah').checked==true){
		$('.check-kategori').prop('checked', true);
		$('#check-kategori-all-atas').prop('checked', true);
	}else{
		$('.check-kategori').prop('checked', false);
		$('#check-kategori-all-atas').prop('checked', false);
	}
}

function set_edit(data){
	console.log(data);
	$('#id-kategori').val(data.id);
	$('#nama-kategori').val(data.nm);
	$('#slug-kategori').val(data.slug);
	$('#induk-kategori option[value='+data.id_idk+']').prop('selected', true);
	$('#deskripsi-kategori').val(data.des);
	$('#icon-menu').val(data.icon);
}

function set_delete(data){
	$('#id_del').val(data.id);
	$('#nama_del').html(data.nm);
	$('#form_action_del').attr('action',data.act);
}

function set_icon(nama){
	$('#icon-menu').val(nama);
}

function isi(data){
	//console.log(data);
	var user = "<?php echo $this->session->userdata('level');?>";
	if(data.error==0){
		table = '';
		select_kategori = '<select id="induk-kategori" name="induk_kategori" class="form-control square-btn-adjust auto-width" title="Induk kategori">'
		select_kategori += '<option value="0">- kategori induk -</option>';
		$.each(data.kategori, function(i,n){
			select_kategori += '<option id="kat-'+n['id_kategori']+'" value="'+n['id_kategori']+'">'+n['kategori']+'</option>';

			table += '<tr id="hapus-row-kategori-'+n['id_kategori']+'" onmouseover="row_actions_over(\''+n['id_kategori']+'\')" onmouseout="row_actions_blur(\''+n['id_kategori']+'\')">';
				
				if(user!='user'){
					table +='<td>';
					if(n['id_kategori']!='1'){
						table +='<input name="id_hapus_kategori_massal_['+n['id_kategori']+']" class="check-kategori" type="checkbox" value="'+n['id_kategori']+'">';
					}
					table +='</td>';
				}
				table +='<td><p>';
					if(n['icon']!=''){
						if(n['icon'].substr(0,2)=='fa'){
							table+='<i width="30px" height="30px" class="fa '+n['icon']+'" style="margin-right:10px"></i>';
						}else{
							table+='<img width="30px" height="30px" src="'+n['icon']+'" style="margin-right:10px">';
						}
					}
					table+=n['kategori']+'</p>'
					+'<br id="br-'+n['id_kategori']+'">'
					+'<div class="row-actions" id="row-actions-'+n['id_kategori']+'" style="display:none;">'
						+'<span class="sunting"><a onclick="set_edit({'
							+'id:\''+n['id_kategori']
							+'\',nm:\''+n['kategori']
							+'\',slug:\''+n['slug']
							+'\',icon:\''+n['icon']
							+'\',id_idk:\''+n['parent']
							+'\',des:\''+n['deskripsi']
							+'\''
						+'})">Sunting</a></span>';
						if(n['id_kategori']!='1'){
							if(penulis=='administrator'){
								table +='<span class="hapus"> | <a title="Hapus '+n['kategori']+'" data-toggle="modal" data-target=".bs-hapus-" onclick="set_delete({id:\''+n['id_kategori']+'\',nm:\''+n['kategori']+'\',act:\''+url+'kategori/delkategori\',element:\'hapus-row-kategori-\'})">Hapus</a></span>'
							}
						}
						table +='<span class="tampilkan"> | <a target="blank" href="'+url+'home/kategori/'+n['id_kategori']+'/'+n['slug']+'">Tampilkan</a></span>'
					+'</div>'
				+'</td>'
				+'<td>'+n['deskripsi']+'</td>'
				+'<td>'+n['slug']+'</td>'
			+'</tr>';
			
			kat_id(n['id_kategori'],0);
		});
		select_kategori += '</select>';

		$("#induk-kategori").html(select_kategori);
		$("#tbody-kategori").html(table);
		$("#jml-list-bawah").html(0);
	}else{
		table = '';
			table += '<tr>'
				+'<td colspan="4">Kategori tidak ditemukan.</td>'
			+'</tr>';
		table += '';
		$("#tbody-kategori").html(table);
	}
}

function kat_id(id,i){
	$.ajax({
        'type': 'POST',
        'url': '<?php echo site_url("kategori/getkategori");?>',
        'dataType': 'json',
        'data': 'id_kat='+id,
        success: function(data){
        	j=i+1;
        	select_kategori = '';
        	table = '';
			$.each(data.data.kategori, function(i,n){
				strip='';
				strip2='';
				for(k=0;k<j;k++){
					strip+='-';
					strip2+='<i class="fa fa-minus"></i> ';
				}
				select_kategori += '<option id="kat-'+n['id_kategori']+'" value="'+n['id_kategori']+'">'+strip+n['kategori']+'</option>';

				table += '<tr id="hapus-row-kategori-'+n['id_kategori']+'" onmouseover="row_actions_over(\''+n['id_kategori']+'\')" onmouseout="row_actions_blur(\''+n['id_kategori']+'\')">'
					+'<td>';
					if(n['id_kategori']!='1'){
						table +='<input name="id_hapus_kategori_massal_['+n['id_kategori']+']" class="check-kategori" type="checkbox" value="'+n['id_kategori']+'">';
					}
					table +='</td>'
					+'<td><p>'+strip2;
					if(n['icon']!=''){
						if(n['icon'].substr(0,2)=='fa'){
							table+='<i width="30px" height="30px" class="fa '+n['icon']+'" style="margin-right:10px"></i>';
						}else{
							table+='<img width="30px" height="30px" src="'+n['icon']+'" style="margin-right:10px">';
						}
					}
					table+=n['kategori']+'</p>'
						+'<br id="br-'+n['id_kategori']+'">'
						+'<div class="row-actions" id="row-actions-'+n['id_kategori']+'">'
							+'<span class="sunting"><a onclick="set_edit({'
								+'id:\''+n['id_kategori']
								+'\',nm:\''+n['kategori']
								+'\',slug:\''+n['slug']
								+'\',icon:\''+n['icon']
								+'\',id_idk:\''+n['parent']
								+'\',des:\''+n['deskripsi']
								+'\''
							+'})">Sunting</a></span>';
							if(n['id_kategori']!='1'){
								if(penulis=='administrator'){
									table +='<span class="hapus"> | <a title="Hapus '+n['kategori']+'" data-toggle="modal" data-target=".bs-hapus" onclick="set_del({id:\''+n['id_kategori']+'\',nm:\''+n['kategori']+'\',act:\''+url+'kategori/delkategori\',element:\'hapus-row-kategori-\'})">Hapus</a></span>'
								}
							}
							table +='<span class="tampilkan"> | <a target="blank" href="'+url+'lihat/kategori/'+n['slug']+'#li-'+n['id_kategori']+'">Tampilkan</a></span>'
						+'</div>'
					+'</td>'
					+'<td>'+n['deskripsi']+'</td>'
					+'<td>'+n['slug']+'</td>'
				+'</tr>';

				kat_id(n['id_kategori'],j);
			});
			$(select_kategori).insertAfter("#kat-"+id);

			$(table).insertAfter("#hapus-row-kategori-"+id);

			$("#jml-list-bawah").html(eval($("#jml-list-bawah").html())+1);
        }
    });
}
</script>